INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (1, '경기도 강화군', '강화군은 경기도 북서쪽에 위치하여 한강 하구와 맞닿아 있으며, 인구수는 약 7만 명, 면적은 약 411㎢입니다. 서울과 인접하여 접근성이 뛰어나며, 전통문화와 자연경관이 어우러진 역사 깊은 지역입니다. 해안선이 길어 갯벌과 농업이 조화롭게 이루어져 있으며, 친환경 농업이 활성화되어 있습니다.', '강화군은 서울과 가까워 생활 인프라 접근성이 우수하며, 귀농 초기 정착을 위한 다양한 지원 프로그램이 잘 마련되어 있습니다. 특히 초보 귀농인을 위한 농업 기술 교육과 다양한 농업 연구 지원이 활발하여 안정적인 농업 기반을 마련할 수 있습니다. 또한 친환경 농업 활성화 정책으로 청정 농산물 생산에 유리한 조건을 갖추고 있어 가족 단위 귀농에 적합한 지역입니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (2, '인천광역시 옹진군', '옹진군은 인천광역시 소속의 군으로 서해안 다도해를 이루는 섬 지역으로 구성되어 있습니다. 인구수는 약 2만 명이며, 면적은 약 1,041㎢입니다. 다양한 크고 작은 섬들로 이루어져 있어 해산물 생산과 어업이 주요 산업이며, 일부 섬에서는 감자와 옥수수 재배도 이루어지고 있습니다.', '옹진군은 어업과 농업이 조화를 이루어 귀어와 귀농을 동시에 고려할 수 있는 독특한 환경을 제공합니다. 특히 해양 관광과 농업을 융합한 창업 기회가 많아 다양한 경제 활동을 병행할 수 있습니다. 어촌 마을에서 제공하는 다양한 지원 사업과 자립형 생활 기반 조성 프로그램이 잘 마련되어 있어 정착에 유리합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (3, '경기도 평택시', '평택시는 경기도 남서부에 위치하여 항구도시와 내륙도시의 특성을 동시에 갖춘 지역입니다. 인구수는 약 56만 명, 면적은 약 452㎢입니다. 평야 지대가 넓어 농업 생산이 활발하며, 기후가 온화하여 다양한 작물 재배에 적합합니다.', '평택시는 귀농자를 위한 다양한 지원 정책과 교육 프로그램이 마련되어 있어 초기 정착이 용이합니다. 농산물 유통망이 잘 갖춰져 있어 생산된 농산물을 안정적으로 판매할 수 있으며, 항만과 인접하여 수출 가능성도 큽니다. 특히, 스마트 농업 지원 사업이 활성화되어 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (4, '경기도 남양주시', '남양주시는 경기도 북동부에 위치하며 인구수는 약 74만 명, 면적은 약 458㎢입니다. 서울과의 접근성이 뛰어나며 자연환경이 풍부하여 귀농 인구가 지속해서 증가하고 있는 지역입니다. 기후는 온난하며 다양한 농작물 재배에 적합합니다.', '남양주시는 귀농·귀촌을 위한 체계적인 교육 프로그램과 다양한 농업 지원 정책이 마련되어 있어 초보 농업인도 부담 없이 시작할 수 있습니다. 특히 수도권과의 근접성 덕분에 농산물 직거래와 도매시장 판매가 수월하여 안정적인 소득 기반을 마련할 수 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (5, '경기도 파주시', '파주시는 경기도 북서부에 위치하며 인구수는 약 48만 명, 면적은 약 672㎢입니다. 서울과 인접하여 교통이 편리하며, 임진강과 한강이 흐르는 지역으로 자연경관이 뛰어납니다. 온화한 기후 덕분에 다양한 작물 재배가 가능하며, 특히 채소 재배가 활발합니다.다.', '파주시는 귀농자 지원 정책과 농업 교육 프로그램이 체계적으로 운영되어 있어 초기 정착이 용이합니다. 특히 친환경 농업과 스마트 농업 기술 도입이 활발하여 청년 귀농자들에게 매력적인 지역으로 평가받고 있습니다. 인근 대도시와의 물류망이 잘 구축되어 있어 농산물 유통이 용이합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (6, '경기도 이천시', '이천시는 경기도 동남부에 위치한 시로, 쌀 농사와 도자기 산업으로 유명합니다. 인구수는 약 22만 명이며, 면적은 약 461㎢입니다. 중부 내륙에 자리 잡고 있어 사계절 기후가 뚜렷하며, 비옥한 평야 지대 덕분에 다양한 작물 재배에 적합합니다. 수도권과의 접근성이 좋아 물류와 농산물 직거래가 활발하게 이루어집니다.', '이천시는 귀농인을 위한 다양한 지원 정책과 농업 기술 교육 프로그램이 잘 마련되어 있어 안정적인 농업 활동을 시작할 수 있습니다. 특히 이천쌀 브랜드 육성을 통해 안정적인 판로 확보가 가능하며, 수도권 인근 시장 접근성도 뛰어납니다. 농촌 생활과 도시 생활을 모두 누릴 수 있는 최적의 입지 조건입니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (7, '경기도 안성시', '안성시는 경기도 남부에 위치한 시로, 축산업과 전통 농업이 발달한 지역입니다. 인구수는 약 19만 명이며, 면적은 약 553㎢입니다. 온난한 기후와 넓은 평야 덕분에 다양한 작물 재배가 가능하며, 특히 배와 오이 생산량이 풍부합니다.', '안성시는 귀농인 지원 정책이 잘 갖춰져 있어 초기 정착 부담이 적습니다. 농업 기술 센터와 귀농 교육 프로그램이 활발하여 초보 귀농자도 안심하고 시작할 수 있습니다. 또한, 수도권과의 접근성이 좋아 농산물 유통이 용이합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (8, '경기도 김포시', '김포시는 경기도 서북부에 위치하며 한강과 인접한 도시로, 인구수는 약 47만 명이며 면적은 약 276㎢입니다. 서울과 인접하여 접근성이 뛰어나며, 도시와 농촌이 조화를 이루어 농업과 생활이 공존하는 지역입니다.', '김포시는 수도권 접근성이 좋아 농산물 직거래 활성화에 유리하며, 대규모 농업과 소규모 특화 농업이 공존하는 특징이 있습니다. 특히 대파와 미나리 등 채소류의 생산량이 많아 안정적인 판로를 확보할 수 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (9, '경기도 화성시', '화성시는 경기도 남서부에 위치하며, 인구수는 약 92만 명으로 면적은 689㎢에 달합니다. 해안과 평야가 조화를 이루며 다양한 작물 재배에 유리한 기후를 가지고 있습니다. 농촌과 도시가 공존하여 농업 생산과 생활 인프라가 모두 잘 갖춰져 있습니다.', '화성시는 귀농 교육과 정착 지원 프로그램이 잘 마련되어 있어 안정적인 귀농 생활이 가능합니다. 또한 친환경 농업이 활성화되어 있어 지속 가능한 농업 활동을 할 수 있는 환경이 마련되어 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (10, '경기도 광주시', '광주시는 경기도 동부에 위치하며 인구수는 약 38만 명, 면적은 430㎢입니다. 서울과 인접하여 교통이 편리하며, 산지와 평야가 어우러져 농업과 주거가 공존하는 지역입니다. 자연 친화적인 전원생활이 가능한 곳으로 귀농 인구가 증가하는 추세입니다.', '광주시는 수도권 접근성이 뛰어나 농산물 직거래가 용이하며, 다양한 농업 교육과 체험 프로그램이 마련되어 있어 초보 귀농자도 부담 없이 시작할 수 있습니다. 특히 산지에서 재배되는 채소와 과일의 수요가 높아 안정적인 판매가 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (11, '경기도 양주시', '양주시는 경기도 북부에 위치하며 인구수는 약 23만 명, 면적은 약 310㎢입니다. 서울과 근접해 있으며, 자연 환경이 잘 보존되어 있어 전원생활을 선호하는 귀농자들에게 인기가 있습니다. 온난한 기후 덕분에 채소와 과일 재배가 활발합니다.', '양주시는 농업 지원 정책이 체계적으로 마련되어 있어 귀농 준비부터 정착까지 단계별로 도움을 받을 수 있습니다. 특히 수도권 근접성 덕분에 농산물 직거래와 유통이 용이하여 수익성을 높일 수 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (12, '경기도 포천시', '포천시는 경기도 북부에 위치하며 인구수는 약 15만 명, 면적은 826㎢입니다. 산림이 풍부하여 청정 농업이 발달해 있으며, 다양한 축산업과 농업이 공존하는 지역입니다. 최근 귀농 인구가 늘어나며 농업 기반 확충이 활발히 이루어지고 있습니다.', '포천시는 산림 자원을 활용한 친환경 농업과 특용 작물 재배가 활발하며, 다양한 농업 교육 프로그램과 지원 정책이 잘 마련되어 있어 귀농자들에게 유리합니다. 자연 관광과 결합한 체험 농업도 활발히 운영 중입니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (13, '경기도 여주시', '여주시는 경기도 남동부에 위치하며 인구수는 약 11만 명, 면적은 608㎢입니다. 수도권과 인접하여 접근성이 뛰어나며, 농업 중심 지역으로 다양한 작물이 재배됩니다. 청정 자연과 전통이 공존하는 곳입니다.', '여주시는 귀농 정책과 지역 특산물 육성 사업이 잘 마련되어 있어 안정적인 농업 기반을 갖추고 있습니다. 특히 여주쌀과 고구마 생산이 활발하여 브랜드화된 농산물 판매가 용이합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (14, '경기도 연천군', '연천군은 경기도 최북단에 위치하며 인구수는 약 4만 명, 면적은 674㎢입니다. 청정 자연을 기반으로 한 농업이 발달하였으며, 온화한 기후와 넓은 농경지가 있어 다양한 작물 재배에 적합합니다.', '연천군은 귀농자를 위한 지원 프로그램과 농업 교육이 활성화되어 있어 초보 농업인도 안정적으로 정착할 수 있습니다. 청정 농산물 브랜드를 육성하여 판매 판로 확보에도 유리합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (15, '경기도 가평군', '가평군은 경기도 북동부에 위치하며 인구수는 약 6만 명, 면적은 약 843㎢입니다. 자연경관이 수려하여 관광자원이 풍부하며, 산림이 많아 청정 농업이 발달하였습니다. 특히 과수 농업과 고랭지 채소 재배가 활발합니다.', '가평군은 농업 기반 시설이 잘 갖춰져 있으며, 청정 자연환경을 활용한 친환경 농업이 발달해 있습니다. 귀농자 지원 프로그램과 농업 교육이 잘 운영되어 있어 안정적인 정착이 가능하며, 농업과 관광을 연계한 6차 산업 활성화 기회도 많습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (16, '경기도 양평군', '양평군은 경기도 동부에 위치하며 인구수는 약 11만 명, 면적은 약 878㎢입니다. 한강과 접해 있어 수자원이 풍부하고, 농촌과 도시가 조화를 이루며 자연경관이 수려합니다. 온화한 기후와 비옥한 토양 덕분에 다양한 작물 재배가 이루어지고 있습니다.', '양평군은 농업과 관광이 조화를 이루며 귀농·귀촌 활성화 정책이 적극적으로 추진되고 있습니다. 특히 친환경 농업과 유기농 생산이 활성화되어 있어 건강한 농산물 재배로 안정적인 소득을 기대할 수 있습니다. 수도권과의 접근성이 좋아 농산물 직거래가 용이합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (17, '강원도 춘천시', '춘천시는 강원도 북서부에 위치하며 인구수는 약 28만 명, 면적은 약 1,116㎢입니다. 서울과 가깝고 교통이 편리하여 귀농·귀촌 지역으로 인기가 많습니다. 기후는 온화하고 강수량이 풍부하여 다양한 작물 재배가 가능합니다.', '춘천시는 다양한 농업 지원 프로그램이 마련되어 있으며, 귀농자들에게 초기 정착 자금을 지원하여 부담을 덜어줍니다. 또한, 수도권과의 교통망이 잘 연결되어 있어 생산된 농산물을 직거래로 판매하기 용이합니다. 농업 기술 교육과 현대화된 시설도 갖춰져 있어 안정적으로 농업을 시작할 수 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (18, '강원도 원주시', '원주시는 강원도 남서부에 위치하며 인구수는 약 35만 명, 면적은 약 872㎢입니다. 수도권과 연결되는 교통 인프라가 잘 갖춰져 있어 물류와 농산물 유통이 용이합니다. 온난한 기후로 과일과 채소 재배에 적합합니다.', '원주시는 귀농·귀촌 활성화를 위한 다양한 지원 정책과 프로그램이 마련되어 있어 초기 정착이 용이합니다. 특히 복숭아와 배 생산이 활발하며, 농산물 직거래 시장과 유통망이 잘 구축되어 있어 안정적인 수익 창출이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (19, '강원도 강릉시', '강릉시는 강원도 동해안에 위치하여 인구수는 약 21만 명, 면적은 약 1,040㎢입니다. 해안과 산이 어우러진 자연환경 덕분에 해산물뿐 아니라 다양한 농산물이 생산됩니다. 기후는 대체로 온화하며 바람이 강한 편입니다', '강릉시는 농산물 직거래와 로컬푸드 판매점이 활성화되어 있어 생산된 농산물을 직접 판매할 수 있는 기회가 많습니다. 특히 배추와 무 재배가 활발하며, 정부 지원 사업도 다양하게 제공되어 안정적인 농업 생활을 시작할 수 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (20, '강원도 삼척시', '삼척시는 강원도 남동부에 위치하며 인구수는 약 6만 명, 면적은 약 1,190㎢입니다. 동해안과 접해 있어 해산물뿐 아니라 농업도 활발히 이루어지고 있습니다. 대체로 온화한 해양성 기후를 보이며 다양한 작물 재배가 가능합니다.', '삼척시는 귀농자를 위한 다양한 교육 프로그램과 농업 기술 지원이 잘 갖춰져 있어 초보 농업인도 부담 없이 정착할 수 있습니다. 해양자원을 활용한 복합 농업도 가능하여 농어업을 병행하는 데 유리합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (21, '강원도 홍천군', '홍천군은 강원도 중부 내륙에 위치하며 인구수는 약 7만 명, 면적은 약 1,819㎢입니다. 산림이 많아 고랭지 농업이 발달해 있으며, 청정 자연환경을 기반으로 친환경 농업이 활발히 이루어지고 있습니다.', '홍천군은 청정 자연을 활용한 친환경 농업과 고랭지 채소 재배가 활발합니다. 농업 기반 시설과 현대화된 생산 설비를 통해 안정적인 농업을 운영할 수 있으며, 다양한 보조금과 지원 정책이 마련되어 있어 정착이 용이합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (22, '강원도 횡성군', '횡성군은 강원도 남서부에 위치하며 인구수는 약 4만 명, 면적은 약 997㎢입니다. 고랭지 채소 재배가 활발하며, 축산업도 동시에 이루어져 복합 농업이 가능합니다. 기후는 대체로 온화하지만 겨울에는 다소 추운 편입니다.', '횡성군은 귀농자를 위해 현대식 농업 기술을 지원하며, 축산업과 채소 농업을 병행할 수 있는 복합 농업 모델이 잘 발달되어 있습니다. 정부와 지방자치단체의 지원 사업이 활발하여 안정적인 귀농 생활이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (23, '강원도 영월군', '영월군은 강원도 남부에 위치하며 인구수는 약 4만 명, 면적은 약 1,126㎢입니다. 산악 지형과 계곡이 많아 자연환경이 수려하며, 기후는 온화하고 강수량이 적당하여 농업에 유리합니다.', '영월군은 전통 농업과 현대 농업이 조화를 이루고 있으며, 사과와 배추 재배가 활발합니다. 지역 농산물 브랜드화 사업이 추진되어 판로 확보가 용이하며, 다양한 농업 교육 프로그램이 마련되어 있어 초보 귀농자에게 적합합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (24, '강원도 평창군', '평창군은 강원도 중부에 위치하며 인구수는 약 4만 명, 면적은 약 1,464㎢입니다. 고랭지 채소 재배와 축산업이 활발하며, 대관령 일대는 여름에도 서늘한 기후를 유지합니다. 겨울철 기온이 낮아 겨울 작물 재배에 유리합니다.', '평창군은 고랭지 채소 재배에 최적화된 환경을 갖추고 있으며, 대규모 농업 단지와 현대화된 농업 설비가 구축되어 있어 안정적인 생산 기반을 갖추고 있습니다. 또한 귀농자를 위한 다양한 지원 프로그램과 농업 교육이 활성화되어 있어 정착이 용이합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (25, '강원도 정선군', '평창군은 고랭지 채소 재배에 최적화된 환경을 갖추고 있으며, 대규모 농업 단지와 현대화된 농업 설비가 구축되어 있어 안정적인 생산 기반을 갖추고 있습니다. 또한 귀농자를 위한 다양한 지원 프로그램과 농업 교육이 활성화되어 있어 정착이 용이합니다.', '정선군은 고랭지 채소와 사과 재배가 활발하며, 정부의 다양한 귀농 지원 사업이 시행되고 있어 초기 정착 부담이 적습니다. 또한, 전통 농업과 현대 농업이 조화를 이루고 있어 다양한 작물을 선택하여 재배할 수 있는 이점이 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (26, '강원도 철원군', '철원군은 강원도 북부에 위치하며 인구수는 약 4만 명, 면적은 약 889㎢입니다. 한탄강과 평야 지대가 넓어 농업이 발달하였으며, 특히 논농사와 밭작물 재배가 활발합니다. 겨울철 기온이 낮아 내한성 작물 재배에 적합합니다.', '철원군은 넓은 평야 지역을 활용한 대규모 농업이 가능하며, 정부의 농업 기반 시설 확충 지원이 활발합니다. 농업 기술센터에서 다양한 교육 프로그램을 제공하여 신기술 도입에 유리하며, 직거래 활성화로 안정적인 판로 확보가 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (27, '강원도 화천군', '화천군은 강원도 북부에 위치하며 인구수는 약 2만 명, 면적은 약 1,000㎢입니다. 산악 지형과 청정 자연환경 덕분에 고랭지 농업과 친환경 농업이 발달해 있습니다. 겨울에는 춥지만 여름에는 비교적 서늘합니다.', '화천군은 청정 자연을 활용한 친환경 농업이 활성화되어 있으며, 특히 호박과 복숭아 재배가 활발합니다. 정부와 지방자치단체의 적극적인 농업 지원 사업 덕분에 귀농 초기 정착 부담이 적으며, 농업 교육과 기술 지원이 잘 이루어져 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (28, '강원도 양구군', '양구군은 강원도 북부에 위치하며 인구수는 약 2만 명, 면적은 약 699㎢입니다. 수박 재배로 유명하며, 비교적 온화한 기후를 가지고 있어 다양한 작물 재배가 가능합니다. 주변 산악 지역을 활용한 고랭지 농업도 발달해 있습니다.', '양구군은 수박과 오이 재배에 적합한 환경을 갖추고 있으며, 직거래 장터와 농산물 유통망이 잘 구축되어 있어 농가 소득 창출에 유리합니다. 또한, 귀농인을 위한 기술 교육과 지원 프로그램이 활성화되어 있어 안정적인 귀농 생활이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (29, '강원도 인제군', '인제군은 강원도 북동부에 위치하며 인구수는 약 3만 명, 면적은 약 1,646㎢입니다. 청정 자연과 맑은 물 덕분에 친환경 농업이 활성화되어 있으며, 고랭지 작물 재배가 활발합니다. 겨울철 기온이 낮아 내한성 작물 재배에 적합합니다.', '인제군은 풋고추와 토마토 재배에 강점을 가지고 있으며, 정부와 지자체의 적극적인 농업 지원 덕분에 초기 정착 부담이 적습니다. 청정 환경을 활용한 농업이 가능하여 친환경 농산물 생산에 유리합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (30, '강원도 고성군', '고성군은 강원도 북동부에 위치하며 인구수는 약 3만 명, 면적은 약 667㎢입니다. 해안선을 따라 위치하여 해양성 기후의 영향을 받으며, 온화한 기후로 다양한 작물 재배에 유리합니다.', '고성군은 해양성 기후를 활용하여 피망과 양배추 재배가 활발하며, 해안가와 인접하여 농업과 어업을 병행할 수 있는 여건이 마련되어 있습니다. 귀농자를 위한 기술 지원과 시설 개선 사업이 활발히 이루어지고 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (31, '강원도 양양군', '양양군은 강원도 동해안에 위치하며 인구수는 약 2만 명, 면적은 약 628㎢입니다. 산과 바다가 어우러진 자연환경 덕분에 해산물과 농산물이 동시에 발달하였습니다. 대체로 온화한 기후로 다양한 농작물 재배가 가능합니다.', '양양군은 배추와 양배추 재배에 적합한 토양과 기후를 가지고 있으며, 귀농자들에게 초기 자금을 지원하여 정착을 돕고 있습니다. 또한 농업 기술 교육과 시설 지원이 활성화되어 있어 안정적인 농업 경영이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (32, '충청북도 청주시', '청주시는 충청북도의 도청 소재지로, 인구수는 약 85만 명, 면적은 약 940㎢입니다. 충북의 중심지로서 교통 인프라가 우수하며, 사계절이 뚜렷한 기후로 다양한 작물 재배가 가능합니다. 농업뿐만 아니라 산업과 교육 중심지로도 발달했습니다.', '청주시는 농업 기반 시설이 잘 갖춰져 있어 대규모 농업 및 가공업이 활발히 이루어지고 있습니다. 귀농자들에게는 농업 지원 프로그램과 농산물 직거래 시스템이 잘 마련되어 있어 안정적인 농업 소득 창출이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (33, '충청북도 충주시', '충주시는 충청북도 북부에 위치하며, 인구수는 약 20만 명, 면적은 약 984㎢입니다. 중부 내륙 고속도로와 중앙선 철도가 교차하여 교통이 편리하며, 사과와 복숭아 생산지로 유명합니다.', '충주시는 사과와 복숭아 같은 과수 재배에 적합한 기후와 토양을 가지고 있으며, 대규모 과수원이 많이 분포해 있습니다. 특히 귀농인을 위한 농업 교육과 창업 지원 프로그램이 잘 마련되어 있어 초보 농업인도 안정적으로 정착할 수 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (34, '충청북도 제천시', '제천시는 충청북도 북동부에 위치하며, 인구수는 약 13만 명, 면적은 약 882㎢입니다. 산간 지역 특유의 기후로 인삼과 약초 재배가 활발하며, 깨끗한 물과 공기를 자랑합니다.', '제천시는 약초와 인삼 재배가 활발하여 한방 산업과 연계된 농업이 강점입니다. 지역 특산물을 활용한 가공식품 산업도 발달해 있어 부가가치 창출이 용이하며, 귀농자들을 위한 체계적인 농업 교육과 지원이 잘 마련되어 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (35, '충청북도 보은군', '보은군은 충청북도 남부에 위치하며, 인구수는 약 3만 명, 면적은 약 793㎢입니다. 전형적인 농업 지역으로 사과와 복숭아 재배가 활발하며, 청정 자연환경을 활용한 친환경 농업이 발달해 있습니다.', '보은군은 사과와 복숭아 재배에 적합한 기후와 토양 조건을 갖추고 있으며, 정부와 지자체의 귀농 정책이 잘 마련되어 있어 초기 정착이 용이합니다. 농업기술센터에서 다양한 작물 재배 교육을 지원하여 안정적인 농업 경영이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (36, '충청북도 옥천군', '옥천군은 충청북도 남부에 위치하며, 인구수는 약 5만 명, 면적은 약 536㎢입니다. 금강이 지나며 기후가 온화하여 다양한 농작물 재배에 유리한 환경입니다. 복숭아와 깻잎 재배가 주를 이룹니다.', '옥천군은 복숭아와 깻잎 재배로 농가 소득을 창출하고 있으며, 친환경 농업 기술 도입이 활발합니다. 귀농자를 위한 주거 지원과 영농 교육이 체계적으로 이루어져 있어 안정적인 정착이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (37, '충청북도 영동군', '영동군은 충청북도 남서부에 위치하며, 인구수는 약 4만 명, 면적은 약 845㎢입니다. 포도와 복숭아 재배가 활발하며, 청정 자연을 활용한 친환경 농업이 발전하고 있습니다.', '영동군은 포도와 복숭아의 주산지로서 과수 재배 기술이 발달해 있으며, 귀농자들에게 농업 기술과 시설 지원이 잘 이루어져 있습니다. 농산물 직거래 활성화로 안정적인 판로 확보가 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (38, '충청북도 증평군', '증평군은 충청북도 중부에 위치하며, 인구수는 약 3만 명, 면적은 약 81㎢로 비교적 작은 규모입니다. 고구마와 대파 재배가 활발하며, 중부 고속도로와 인접하여 교통이 편리합니다.', '증평군은 소규모 농업을 통해 고구마와 대파 등의 작물 생산에 집중하고 있으며, 귀농 초기 자금 지원과 정착 지원이 활발합니다. 농업 교육 프로그램도 다양하여 초보 농업인에게 적합한 환경을 제공합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (39, '충청북도 진천군', '진천군은 충청북도 중부에 위치하며, 인구수는 약 8만 명, 면적은 약 407㎢입니다. 교통이 편리하여 농산물 유통이 활발하며, 오이와 수박 생산량이 많습니다.', '진천군은 오이와 수박을 비롯하여 다양한 채소 생산이 활발하며, 직거래 판매 활성화로 농가 소득 증대가 가능합니다. 또한 귀농자를 위한 자금 지원과 농업 기술 교육이 잘 갖춰져 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (40, '충청북도 괴산군', '괴산군은 충청북도 중부에 위치하며, 인구수는 약 3만 명, 면적은 약 842㎢입니다. 산간 지역 특유의 청정 환경 덕분에 옥수수와 배추 재배가 활발합니다.', '괴산군은 옥수수와 배추 같은 고랭지 작물 재배가 활발하며, 정부와 지자체의 귀농 지원 사업이 적극적으로 추진되고 있습니다. 농업 교육과 지원 프로그램이 잘 갖춰져 있어 귀농 정착에 유리합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (41, '충청북도 음성군', '음성군은 충청북도 중부에 위치하며, 인구수는 약 9만 명, 면적은 약 521㎢입니다. 농업과 공업이 공존하는 지역으로, 수박과 복숭아 생산이 활발합니다', '음성군은 수박과 복숭아의 주산지로서, 직거래와 온라인 판매가 활성화되어 있어 농가 소득이 안정적입니다. 또한 귀농자를 위한 시설 지원과 기술 교육이 잘 이루어져 있어 안정적인 농업 경영이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (42, '충청북도 단양군', '단양군은 충청북도 북동부에 위치하며, 인구수는 약 3만 명, 면적은 약 780㎢입니다. 산악지형이 많아 고랭지 농업이 발달해 있으며, 사계절 기온 차가 커 사과와 수박 재배에 적합합니다.', '단양군은 사과와 수박 재배가 활발하며, 특히 고랭지 농업이 강점입니다. 귀농자들을 위한 체계적인 교육 프로그램과 재정 지원이 마련되어 있어 안정적인 농업 기반을 마련할 수 있습니다. 농업기술센터에서 기술 교육을 지원하여 초보 농업인에게 적합한 환경입니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (43, '충청남도 천안시', '천안시는 충청남도 북부에 위치하며, 인구수는 약 70만 명, 면적은 약 636㎢입니다. 기후는 온화하며, 연평균 기온은 약 12°C로, 배와 오이 재배에 유리한 환경을 갖추고 있습니다.', '천안시는 배와 오이 같은 고부가가치 작물 재배로 농업 소득이 안정적입니다. 귀농자들을 위한 창업 지원과 기술 교육이 잘 갖춰져 있어 초기 정착이 용이합니다. 또한 인근 농업기술센터에서 재배 교육을 활발히 운영하고 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (44, '충청남도 공주시', '공주시는 충청남도 중앙부에 위치하며, 인구수는 약 11만 명, 면적은 약 864㎢입니다. 대체로 온화한 기후를 보이며, 연평균 기온은 약 13°C로 다양한 과일과 채소를 재배하기 적합합니다.', '공주시는 오이와 배가 주요 농산물로 생산되고 있으며, 지역 특산물 가공 사업도 활발합니다. 귀농자들에게는 정부 지원 사업과 창업 자금 지원이 적극적으로 이루어지고 있어 안정적인 귀농이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (45, '충청남도 보령시', '보령시는 충청남도 서해안에 위치하며, 인구수는 약 10만 명, 면적은 약 569㎢입니다. 해양성 기후의 영향을 받아 겨울이 비교적 온화하여 다양한 채소 재배가 용이합니다.', '보령시는 해양성과 육상 농업이 조화를 이루어 다양한 작물 재배가 가능하며, 농산물 직거래와 로컬 푸드 활성화로 안정적인 판로를 확보하고 있습니다. 귀농자를 위한 주거 지원과 교육 프로그램이 잘 갖춰져 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (46, '충청남도 아산시', '아산시는 충청남도 북서부에 위치하며, 인구수는 약 33만 명, 면적은 약 542㎢입니다. 온화하고 습윤한 기후로 인해 배와 대파 재배에 유리하며, 연평균 기온은 약 13°C입니다.', '아산시는 대규모 농업 기반을 바탕으로 배와 대파 등 고부가가치 작물 재배가 활발합니다. 특히 농업기술센터의 지원으로 신기술을 적용한 스마트팜 도입이 활발하며, 귀농자를 위한 다양한 지원이 이루어지고 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (47, '충청남도 서산시', '서산시는 충청남도 서해안에 위치하며, 인구수는 약 17만 명, 면적은 약 739㎢입니다. 온난한 해양성 기후로 인해 겨울철이 비교적 따뜻하여 채소 재배에 적합합니다.', '서산시는 양배추와 감자 같은 대규모 채소 생산에 적합한 지역으로, 농업 기반 시설이 잘 갖춰져 있습니다. 또한 귀농자들에게는 체계적인 재배 기술 교육과 자립형 귀농 지원이 잘 이루어지고 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (48, '충청남도 논산시', '논산시는 충청남도 남부에 위치하며, 인구수는 약 12만 명, 면적은 약 554㎢입니다. 연평균 기온은 약 13°C로 온화하며, 딸기와 수박 재배에 적합합니다.', '논산시는 딸기 재배를 중심으로 한 시설원예 농업이 발달해 있으며, 귀농자들에게는 전문 농업 교육과 시설 설치 지원이 이루어지고 있습니다. 직거래와 농업 창업 기회가 많아 안정적인 농업 경영이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (49, '충청남도 계룡시', '계룡시는 충청남도 남서부에 위치하며, 인구수는 약 4만 명, 면적은 약 60㎢로 소규모 도시입니다. 연평균 기온은 약 12°C로 농업과 전원생활이 조화를 이루며 다양한 채소 재배가 이루어지고 있습니다.', '계룡시는 귀농자들이 소규모 텃밭 농업을 통해 안정적으로 정착할 수 있는 환경을 갖추고 있으며, 농업기술센터에서 재배 교육과 컨설팅을 적극적으로 지원합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (50, '충청남도 당진시', '당진시는 충청남도 서북부에 위치하며, 인구수는 약 17만 명, 면적은 약 705㎢입니다. 해양성 기후로 온화한 날씨를 보여 다양한 채소 재배가 가능합니다.', '당진시는 무와 고구마 등 채소 생산이 활발하며, 해양 인근 농업 기반이 잘 조성되어 있어 다양한 작물 재배가 가능합니다. 귀농자에게는 안정적인 판로와 직거래 지원이 이루어집니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (51, '충청남도 금산군', '금산군은 충청남도 남부에 위치하며, 인구수는 약 5만 명, 면적은 약 576㎢입니다. 금산은 전국 인삼의 주산지로서, 인삼 재배와 관련된 산업이 발달해 있습니다. 온난한 기후와 청정한 자연환경 덕분에 인삼 외에도 고추, 딸기 등 다양한 농산물 재배가 가능합니다.', '금산군은 인삼 농업으로 유명하며, 정부와 지자체 차원에서 인삼 재배 기술 및 유통 지원을 적극적으로 시행하고 있습니다. 귀농자들을 위해 농업 기술 교육과 창업 지원이 잘 마련되어 있어 농산물 가공업과 직거래 활성화를 통한 수익 창출 기회가 많습니다. 청정 환경과 인삼 테마 관광이 결합된 농촌 경제가 발달해 있어 안정적 정착이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (52, '충청남도 부여군', '부여군은 충청남도 중서부에 위치하며, 인구수는 약 7만 명, 면적은 약 624㎢입니다. 한반도 중부 내륙에 위치하여 대체로 온화하고 건조한 기후를 보이며, 농업이 발달해 있습니다. 특히 수박과 오이, 방울토마토 등이 많이 재배됩니다.', '부여군은 수박과 오이 재배로 유명하며, 귀농자들에게는 체계적인 농업 교육과 지원 프로그램이 마련되어 있습니다. 로컬푸드 직매장과 농산물 가공 지원 센터를 통해 판로 확보와 상품화에 유리하여 안정적인 소득을 기대할 수 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (53, '충청남도 서천군', '서천군은 충청남도 서해안에 위치하며, 인구수는 약 5만 명, 면적은 약 357㎢입니다. 온난한 해양성 기후로 겨울이 따뜻하여 채소 재배가 유리합니다. 해안 지역이 많아 어업과 농업이 조화를 이루고 있는 지역입니다.', '서천군은 쪽파와 강낭콩 재배로 유명하며, 해양과 인접한 농업 기반 덕분에 다양한 채소와 해산물 가공이 가능합니다. 귀농자들에게는 농업기술센터에서의 재배 교육과 가공기술 지원이 활발하여 안정적인 정착이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (54, '충청남도 청양군', '청양군은 충청남도 중앙부에 위치하며, 인구수는 약 3만 명, 면적은 약 479㎢입니다. 사계절 기후 변화가 뚜렷하며, 비교적 온화하여 다양한 농작물 재배에 적합합니다. 특히 고추와 수박이 많이 재배됩니다.', '청양군은 청양 고추로 유명하며, 매운맛과 향이 강해 전국적으로 수요가 많습니다. 농업 기반이 잘 마련되어 있어 귀농자들이 고추 농사를 안정적으로 시작할 수 있으며, 수박과 매실 등 다양한 과일 재배에도 적합한 환경입니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (55, '충청남도 홍성군', '홍성군은 충청남도 서부에 위치하며, 인구수는 약 10만 명, 면적은 약 443㎢입니다. 서해안에 위치하여 해양성 기후를 보이며, 겨울이 비교적 온화하여 다양한 채소 재배에 유리합니다.', '홍성군은 대파와 수박, 배추 재배로 유명하며, 귀농자들을 위한 체계적인 교육 프로그램과 자립 지원이 잘 갖추어져 있습니다. 농업 기술을 배울 수 있는 교육 시설과 함께 지역 특산물 가공 사업이 활발하여 안정적인 수익 창출이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (56, '충청남도 예산군', '예산군은 충청남도 중앙부에 위치하며, 인구수는 약 8만 명, 면적은 약 544㎢입니다. 사계절이 뚜렷하고, 봄과 가을이 온화하여 과수 재배에 적합합니다. 특히 사과와 배 재배가 활발합니다.', '예산군은 사과와 배의 주산지로 유명하며, 농촌체험관광과 연계하여 소득 창출이 가능합니다. 귀농자들을 위해 전문 농업 교육과 스마트팜 도입 지원이 잘 이루어져 있어 최신 기술을 활용한 농업 경영이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (57, '충청남도 태안군', '태안군은 충청남도 서북부에 위치하며, 인구수는 약 6만 명, 면적은 약 504㎢입니다. 서해안에 자리 잡고 있어 해양성 기후의 영향을 받아 여름에는 덥고 겨울에는 비교적 온화한 날씨를 보입니다. 특히 해양 자원을 활용한 어업과 함께 해안 농업이 발달해 있어 다양한 작물 재배가 가능합니다.', '태안군은 해안가를 중심으로 마늘과 무를 많이 재배하며, 해양성 기후로 인한 소금기와 바닷바람의 영향을 받아 품질이 뛰어납니다. 정부 차원의 해양 농업 지원과 스마트 농업 기술 도입이 활발히 진행되어 있어 농업인의 소득 안정성이 높은 편입니다. 귀농자들에게는 전문 교육과 지원 사업이 잘 마련되어 있어 정착이 용이합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (58, '전라북도 군산시', '군산시는 전라북도 서부에 위치하며, 인구수는 약 27만 명, 면적은 약 397㎢입니다. 해안가를 중심으로 어업과 농업이 혼합되어 있으며, 온화하고 바람이 적은 해양성 기후가 특징입니다. 특히 가지와 대파 재배로 유명합니다.', '군산시는 농업과 어업이 조화를 이루어 귀농자에게 다양한 선택지를 제공합니다. 가지와 대파 등 채소류 생산이 활발하며, 수출용 농산물로의 전환을 통해 안정적인 소득을 확보할 수 있습니다. 귀농 초기 정착을 돕기 위한 농업 지원 정책과 도시 인프라를 갖추고 있어 편리하게 정착할 수 있는 장점이 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (59, '전라북도 익산시', '익산시는 전라북도 중서부에 위치하며, 인구수는 약 28만 명, 면적은 약 506㎢입니다. 온난한 기후와 비옥한 토양 덕분에 다양한 작물 재배가 가능합니다. 특히 배추와 고구마가 많이 재배되며, 식품 가공 산업이 발달해 있습니다.', '익산시는 배추와 고구마를 비롯해 다양한 채소류를 재배하기에 적합하며, 현대화된 농업 인프라와 지원 제도가 잘 마련되어 있습니다. 특히 식품 가공 산업과 연계한 창업 기회가 많아 농산물의 부가가치 창출이 용이합니다. 귀농자들에게는 안정적인 소득 기반 마련을 위한 교육과 지원 프로그램이 풍부합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (60, '전라북도 정읍시', '정읍시는 전라북도 남서부에 위치하며, 인구수는 약 11만 명, 면적은 약 692㎢입니다. 사계절이 뚜렷하고 온화한 기후로 과수와 채소 재배에 적합합니다. 특히 수박과 양파가 많이 재배되며, 지역 농산물 브랜드가 활성화되어 있습니다.', '정읍시는 수박과 양파의 주산지로 유명하며, 로컬푸드 직매장과 농산물 유통센터를 통한 안정적 판로 확보가 가능합니다. 귀농자들에게는 지역 농업기술센터에서 다양한 교육과 농업 기계 대여 서비스를 제공하여 초기 정착을 돕고 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (61, '전라북도 남원시', '남원시는 전라북도 동부에 위치하며, 인구수는 약 8만 명, 면적은 약 752㎢입니다. 전형적인 내륙 산간 지역으로, 여름에는 덥고 겨울에는 한랭한 기후를 보입니다. 과수 농업과 특용작물 재배가 활발합니다.', '남원시는 포도와 방울토마토 생산지로 유명하며, 귀농자들에게는 농업 관련 교육과 지원이 잘 마련되어 있습니다. 전통 문화와 농촌 체험이 결합된 마을들이 많아 농촌 관광과 농업을 결합한 수익 모델을 개발할 수 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (62, '전라북도 김제시', '김제시는 전라북도 중앙부에 위치하며, 넓고 평탄한 지형 덕분에 대한민국 최대의 곡창지대로 불립니다. 인구수는 약 8만 명이며, 면적은 약 545㎢입니다. 김제평야를 중심으로 한 벼농사가 발달하였으며, 사계절 뚜렷한 기후와 풍부한 일조량 덕분에 벼와 밭작물 재배가 활발합니다', '김제시는 전통적인 농업 도시로 벼농사에 적합한 지리적 특성과 함께 다양한 농업 지원 정책이 마련되어 있어 안정적으로 귀농 생활을 시작할 수 있습니다. 또한, 친환경 농업 육성 정책과 청년 귀농 지원 프로그램도 잘 갖춰져 있어 젊은 귀농인들에게 매력적인 지역입니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (63, '전라북도 완주군', '완주군은 전라북도의 서북부에 위치하며, 인구수는 약 9만 명이고 면적은 약 821㎢입니다. 전주와 인접해 있어 교통과 생활 인프라가 잘 갖춰져 있으며, 해발 고도가 낮고 평탄하여 농업 활동에 적합합니다. 기후는 사계절이 뚜렷하여 밭작물과 과수 재배에 유리합니다.', '완주군은 귀농인들을 위한 다양한 지원 정책과 체계적인 농업 교육 프로그램이 운영되고 있습니다. 특히 완주군 농업기술센터를 통해 농업 교육과 기술 지원을 받을 수 있으며, 다양한 지역 특산물과 브랜드화 전략으로 소득 창출이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (64, '전라북도 진안군', '진안군은 전라북도 동북부에 위치하며, 인구수는 약 2만 명, 면적은 약 787㎢입니다. 고원 지대에 위치하여 여름에는 서늘하고 겨울에는 춥습니다. 이러한 기후 조건 덕분에 고랭지 농업과 약초 재배가 발달했습니다.', '진안군은 청정 자연환경을 활용한 고랭지 농업과 약초 재배가 특화되어 있습니다. 특히 인삼과 홍삼 재배가 활발하여 관련 가공업도 잘 이루어져 있습니다. 귀농자들에게는 약초 재배와 가공을 연계한 소득 창출 기회가 많아 안정적인 귀농 생활이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (65, '전라북도 무주군', '무주군은 전라북도 동부 산악 지역에 위치하며, 인구수는 약 2만 명입니다. 무주 덕유산을 중심으로 고랭지 농업이 발달해 있으며, 사계절이 뚜렷하고 일교차가 커 과일과 채소 재배에 유리합니다.', '무주군은 고랭지 특성을 이용한 사과와 포도 재배가 활발하며, 자연 친화적인 귀농 환경을 제공합니다. 특히 무주 반딧불축제 등 농촌관광 자원을 활용하여 농업과 관광을 연계한 소득 창출이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (66, '전라북도 장수군', '장수군은 전라북도 동남부에 위치하며, 인구수는 약 2만 5천 명입니다. 해발 400~800m의 고지대에 위치하여 서늘한 기후와 깨끗한 물이 특징입니다. 고랭지 채소와 과수 재배가 발달해 있으며, 특히 장수사과로 유명합니다.', '장수군은 귀농인에게 청정 고랭지 농업의 이점을 제공합니다. 사과를 비롯한 과수 재배가 활발하며, 기후 특성을 이용해 고품질 농산물을 생산할 수 있습니다. 귀농 정착 교육과 농업기술 지원이 잘 이루어져 있어 안정적인 농업 활동이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (67, '전라북도 임실군', '임실군은 전라북도 남부에 위치하며, 인구수는 약 3만 명입니다. 전형적인 농촌 지역으로, 치즈와 유제품 생산지로 유명합니다. 온화한 기후와 비교적 평탄한 지형 덕분에 목축업과 함께 다양한 농작물 재배가 활발합니다.', '임실군은 치즈 산업을 중심으로 한 6차 산업이 발달해 있어 농업과 가공업을 연계하여 소득을 극대화할 수 있습니다. 특히 귀농 지원 정책이 체계적으로 마련되어 있어 청년 귀농인들에게 유리합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (68, '전라북도 순창군', '순창군은 전라북도 남부에 위치하며, 인구수는 약 3만 명으로 면적은 약 547㎢입니다. 순창 고추장으로 유명하며, 전통 발효식품 산업이 발달해 있습니다. 산지가 많아 청정 환경이 유지되며, 다양한 농작물이 재배되는 전형적인 농업 중심 지역입니다.', '순창군은 전통 발효식품 산업과 연계한 농산물 가공업이 활발하며, 특히 고추장과 관련된 고추 재배가 주요 농업 활동입니다. 귀농인을 위한 다양한 지원 프로그램과 교육이 잘 마련되어 있어 초보 귀농자도 쉽게 정착할 수 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (69, '전라북도 고창군', '고창군은 전라북도 서남부에 위치하며, 인구수는 약 5만 명으로 면적은 약 607㎢입니다. 전라북도 최대의 농업 지역 중 하나로, 비옥한 평야가 넓게 펼쳐져 있어 쌀과 보리 농사가 활발합니다. 또한, 수박과 고구마의 생산량이 많아 전국적인 명성을 가지고 있습니다.', '고창군은 귀농 교육과 지원 정책이 체계적으로 마련되어 있어 농업 기반 조성이 용이합니다. 특히, 수박과 고구마 같은 고부가가치 작물 재배를 통해 안정적인 수익 창출이 가능하며, 농업 기술 센터를 통해 지속적인 기술 지원을 받을 수 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (70, '전라북도 부안군', '부안군은 전라북도 서남부에 위치하며, 인구수는 약 5만 명으로 면적은 약 494㎢입니다. 해안선을 따라 펼쳐진 농어촌 지역으로, 양파와 무, 감자 등의 생산이 활발합니다. 따뜻한 해양성 기후로 인해 채소와 과일 재배에 유리한 조건을 갖추고 있습니다.', '부안군은 해양과 농업이 공존하는 지역으로, 귀농과 귀어를 동시에 고려할 수 있는 장점이 있습니다. 다양한 농업 지원 정책과 교육 프로그램이 마련되어 있어 안정적으로 농업 기반을 다질 수 있으며, 인근 항구를 통해 직거래와 유통이 용이합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (71, '전라남도 여수시', '여수시는 전라남도 남동부 해안에 위치하며, 인구수는 약 28만 명으로 면적은 약 510㎢입니다. 해안선이 복잡하게 펼쳐져 있으며, 해양성 기후로 인해 온난하고 습윤한 날씨를 보입니다. 어업과 관광이 주산업이지만, 농업 또한 활발하여 다양한 작물이 재배됩니다.', '여수시는 관광과 농업이 조화를 이루고 있어 농산물 직거래 활성화와 농촌 체험 관광이 결합된 창업 아이디어를 실현하기 좋습니다. 또한, 정부 지원을 통해 친환경 농업 육성 정책이 활발하여 지속 가능한 농업이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (72, '전라남도 순천시', '순천시는 전라남도 남부에 위치하며, 인구수는 약 28만 명으로 면적은 약 907㎢입니다. 온화하고 습윤한 해양성 기후로 인해 다양한 농작물이 자라기 적합하며, 특히 오이와 배, 매실 등이 많이 재배됩니다. 전통 농업과 현대적 농업 기술이 공존하는 지역입니다.', '순천시는 귀농인을 위한 다양한 교육 프로그램과 지원 제도가 마련되어 있으며, 농촌 체험 관광과 연계한 창업 기회도 풍부합니다. 특히, 순천만의 자연환경과 어우러진 친환경 농업이 활성화되어 있어 귀농자들이 안정적으로 정착할 수 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (73, '전라남도 나주시', '나주시는 전라남도 중부에 위치하며, 인구수는 약 11만 명으로 면적은 약 608㎢입니다. 나주 배로 유명하며, 온난하고 습윤한 기후 덕분에 다양한 과채류가 잘 자랍니다. 농업과 함께 전통 문화가 어우러진 지역으로, 친환경 농업도 활발히 이루어지고 있습니다.', '나주시는 농업과 전통 문화가 조화를 이루어 안정적인 농업 활동이 가능합니다. 특히, 나주 배와 같은 브랜드화된 농산물을 통해 안정적인 수익을 창출할 수 있으며, 귀농인을 위한 다양한 교육과 지원 제도가 잘 갖춰져 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (74, '전라남도 광양시', '광양시는 전라남도 동부에 위치하며, 인구수는 약 15만 명으로 면적은 약 456㎢입니다. 여수와 순천을 인접한 교통 요충지로, 철강 산업과 더불어 농업도 활발히 이루어지고 있습니다. 온화하고 따뜻한 해양성 기후로 인해 농작물 재배에 적합하며, 특히 매실과 호박이 많이 생산됩니다.', '광양시는 귀농인 정착 지원이 잘 되어 있어 농업 교육과 기술 습득이 용이합니다. 또한, 광양 매실 브랜드화를 통해 안정적인 소득을 기대할 수 있으며, 지역 농산물 직거래 활성화를 통해 판매 유통 구조도 잘 갖춰져 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (75, '전라남도 담양군', '담양군은 전라남도 북부에 위치하며, 인구수는 약 4만 명으로 면적은 약 455㎢입니다. 전통 대나무 공예로 유명하며, 온화한 기후와 풍부한 수자원 덕분에 농업이 발달한 지역입니다. 특히 딸기와 방울토마토 등 시설 원예작물이 주로 재배됩니다.', '담양군은 농업과 관광이 어우러진 지역으로, 귀농과 귀촌 인구 유입이 활발합니다. 농업 기술 교육과 정착 지원 프로그램이 잘 갖춰져 있어 초보 농업인도 안정적으로 정착할 수 있습니다. 또한, 농산물 가공 산업과 연계하여 부가가치를 높이는 사업이 활발히 이루어지고 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (76, '전라남도 곡성군', '곡성군은 전라남도 북동부에 위치하며, 인구수는 약 2만 6천 명으로 면적은 약 547㎢입니다. 산림과 강이 어우러진 자연 친화적 지역으로, 온화하고 습윤한 기후 덕분에 과수와 채소 재배에 유리합니다. 메론과 수박이 대표적인 작물로 유명합니다.', '곡성군은 농업 기반이 잘 마련되어 있어 작물 재배와 가공이 용이합니다. 특히 지역 특산물로 브랜드화된 메론과 수박의 생산이 활발하여 안정적인 소득 기반을 마련할 수 있습니다. 귀농 정책과 교육 프로그램이 체계적으로 운영되어 있어 새로운 시작을 위한 기반이 잘 마련되어 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (77, '전라남도 구례군', '구례군은 전라남도 동북부에 위치하며, 인구수는 약 2만 명으로 면적은 약 443㎢입니다. 산지가 많고 맑은 물이 흐르는 자연환경 덕분에 친환경 농업이 활성화되어 있습니다. 특히 오이와 호박 재배가 활발하며, 고랭지 채소도 많이 생산됩니다.', '구례군은 청정 자연환경 덕분에 친환경 농업을 선호하는 귀농인에게 적합한 지역입니다. 농산물 직거래 장터와 로컬푸드 매장이 활성화되어 있어 판매 걱정을 덜 수 있습니다. 또한, 체험 농업과 관광을 연계하여 새로운 수익 창출이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (78, '전라남도 고흥군', '고흥군은 전라남도 남부 해안에 위치하며, 인구수는 약 6만 명으로 면적은 약 804㎢입니다. 해양성 기후로 온난하여 겨울에도 비교적 따뜻하며, 다양한 해산물과 함께 농업이 발달한 지역입니다. 고흥 마늘과 고흥 유자가 유명하며, 채소와 과일 재배도 활발합니다.', '고흥군은 귀농·귀촌 지원 정책이 잘 마련되어 있어 초기 정착 부담이 적습니다. 특히 농수산물 직거래가 활발하여 유통 문제가 적고, 정부와 지자체의 귀농인 유치 프로그램을 통해 기술 습득과 정착 지원을 받을 수 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (79, '전라남도 보성군', '보성군은 전라남도 남동부에 위치하며, 인구수는 약 4만 명으로 면적은 약 664㎢입니다. 전국 최대의 녹차 생산지로 유명하며, 온화하고 습윤한 기후 덕분에 채소와 과일 재배가 활발합니다. 또한, 청정 자연 속에서 친환경 농업이 많이 이루어집니다.', '보성군은 녹차뿐만 아니라 다양한 채소류와 특산물이 많이 재배되어 귀농자들에게 다양한 선택지를 제공합니다. 특히 친환경 농업 교육과 지원 정책이 활성화되어 있어 안정적인 귀농 생활을 시작하기에 적합합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (80, '전라남도 화순군', '화순군은 전라남도 중남부에 위치하며, 인구수는 약 6만 명으로 면적은 약 786㎢입니다. 온난하고 습윤한 기후로 다양한 농작물이 잘 자라며, 특히 방울토마토와 파프리카 재배가 활발합니다. 자연환경과 도시 기반이 조화를 이루어 생활 인프라도 잘 갖춰져 있습니다.', '화순군은 귀농 정책과 다양한 농업 교육이 체계적으로 마련되어 있어 초보 농업인도 안정적으로 정착할 수 있습니다. 특히 방울토마토와 파프리카 같은 고부가가치 작물을 재배할 수 있어 소득 창출이 용이합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (81, '전라남도 장흥군', '장흥군은 전라남도 남서부에 위치하며, 인구수는 약 3만 명으로 면적은 약 618㎢입니다. 전형적인 농업 중심 지역으로, 온화한 기후와 풍부한 강수량 덕분에 다양한 작물 재배가 가능합니다. 특히 양파와 감자 재배가 활발하여 농업 생산 기반이 탄탄합니다.', '장흥군은 전통 농업과 현대 농업이 조화를 이루고 있는 지역으로, 귀농자를 위한 다양한 지원 정책과 교육 프로그램이 마련되어 있습니다. 특히 특산물인 양파와 감자 재배에 대한 노하우를 쉽게 배울 수 있으며, 직거래 시장이 잘 발달해 있어 판매 걱정이 적습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (82, '전라남도 강진군', '강진군은 전라남도 남부에 위치하며, 인구수는 약 3만 6천 명으로 면적은 약 500㎢입니다. 온화한 해양성 기후 덕분에 다양한 작물 재배가 가능하며, 특히 호박과 표고버섯 재배가 활발합니다. 전통 문화와 농업이 조화를 이루는 곳입니다.', '강진군은 귀농·귀촌 활성화를 위해 다양한 교육과 지원 사업을 추진하고 있어 초기 정착 부담이 적습니다. 특히 호박과 표고버섯 같은 특산물 재배에 대한 기술 전수가 활발하여 안정적인 소득을 창출할 수 있는 기회가 많습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (83, '전라남도 해남군', '해남군은 전라남도 남단에 위치하며, 인구수는 약 7만 명으로 면적은 약 1,014㎢에 달합니다. 넓은 평야와 온화한 기후 덕분에 다양한 작물 재배가 활발하며, 특히 배추와 고구마가 대표적인 특산물입니다.', '해남군은 귀농인을 위한 지원 정책이 풍부하여 초기 정착이 용이하며, 지역 특산물인 배추와 고구마를 활용한 브랜드 농산물 사업이 활성화되어 있습니다. 또한, 전국 농산물 유통망과 연계된 시스템 덕분에 안정적인 판매가 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (84, '전라남도 영암군', '영암군은 전라남도 중남부에 위치하며, 인구수는 약 5만 명으로 면적은 약 617㎢입니다. 따뜻한 기후와 넓은 평야 덕분에 농업과 축산업이 함께 발달한 지역으로, 고구마와 무의 주산지로 유명합니다.', '영암군은 고구마와 무 재배를 통한 농가 소득 창출이 활발하며, 지역 농산물 가공 산업도 발전해 있습니다. 귀농인을 위한 교육 프로그램과 영농 기술 지원이 체계적으로 마련되어 있어 안정적으로 농업을 시작할 수 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (85, '전라남도 무안군', '무안군은 전라남도 서남부에 위치하며, 인구수는 약 8만 명으로 면적은 약 764㎢입니다. 온난한 해양성 기후 덕분에 양파와 양배추 재배가 활발하며, 국내 최대 양파 생산지로 유명합니다.', '무안군은 귀농을 고려하는 사람들에게 양파 농업을 중심으로 한 안정적인 소득 기반을 제공합니다. 양파와 양배추 재배 노하우를 체계적으로 전수받을 수 있으며, 직거래 시장과 유통망이 잘 갖춰져 있어 수익성도 높습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (86, '전라남도 함평군', '함평군은 전라남도 서부에 위치하며, 인구수는 약 3만 명으로 면적은 약 392㎢입니다. 온화한 기후와 평탄한 지형 덕분에 채소와 과수 재배가 활발하며, 특히 양파와 팽이버섯이 주요 작물입니다.', '함평군은 농업 기반이 탄탄하여 초보 귀농인도 안정적으로 시작할 수 있습니다. 양파와 팽이버섯 등 고수익 작물에 대한 기술 지원이 풍부하여 수익성을 높이기 쉽습니다. 또한, 지역 농산물 축제를 통해 홍보 효과를 극대화할 수 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (87, '전라남도 영광군', '영광군은 전라남도 서북부에 위치하며, 인구수는 약 5만 명으로 면적은 약 478㎢입니다. 청정 자연과 농업 기반이 어우러져 고구마와 배추 등 다양한 작물이 생산되는 지역입니다.', '영광군은 귀농 지원이 활발하며, 농업 교육과 정착 프로그램이 잘 마련되어 있어 초보 귀농인도 쉽게 시작할 수 있습니다. 특히 배추와 고구마 등 다수의 농작물에 대한 전문 교육이 제공되어 소득 안정성이 높습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (88, '전라남도 장성군', '장성군은 전라남도 북서부에 위치하며, 인구수는 약 4만 명으로 면적은 약 520㎢입니다. 온화한 기후와 함께 다양한 농작물 재배가 가능하며, 특히 단감과 떫은감이 주요 특산물입니다.', '장성군은 감 재배에 대한 체계적인 교육과 지원이 잘 마련되어 있어 감 농업으로 안정적인 소득 창출이 가능합니다. 또한 농업 인프라가 잘 갖춰져 있어 귀농 초기 부담이 적습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (89, '전라남도 완도군', '완도군은 전라남도 남부 해안에 위치하며, 인구수는 약 5만 명으로 면적은 약 391㎢입니다. 완도군은 온화한 해양성 기후를 가지고 있어 해조류와 수산물 양식이 활발합니다. 특히 전복과 김 생산이 전국적으로 유명하며, 청정 해역 덕분에 수산업이 주요 소득원입니다.', '완도군은 해양 수산업과 농업이 조화를 이루고 있어 귀어와 귀농을 동시에 고려할 수 있는 환경입니다. 해양 생태와 어촌 문화가 어우러져 관광과 농업이 결합된 6차 산업 육성이 가능하며, 정부와 지자체의 어업 지원도 풍부하여 안정적으로 정착할 수 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (90, '전라남도 진도군', '진도군은 전라남도 서남부에 위치하며, 인구수는 약 3만 명으로 면적은 약 440㎢입니다. 해양성 기후 덕분에 다양한 수산물이 생산되며, 대표적으로 전복과 김, 미역 등의 해조류 양식이 발달해 있습니다.', '진도군은 해양 자원을 적극 활용할 수 있는 귀어 귀촌 지원이 잘 마련되어 있어 안정적인 생활 기반을 마련할 수 있습니다. 특히 해조류 양식의 노하우를 쉽게 접할 수 있어 귀어를 희망하는 사람들에게 적합한 환경입니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (91, '전라남도 신안군', '신안군은 전라남도 서부 해안에 위치하며, 인구수는 약 4만 명으로 면적은 약 573㎢입니다. 다도해를 이루는 수많은 섬으로 구성되어 있어 해양 수산업과 염전 산업이 발달해 있습니다.', '신안군은 해양 자원을 활용한 김 양식과 소금 생산이 매우 활발하며, 귀어 지원 정책도 잘 갖춰져 있습니다. 해양 생태와 어촌 체험 관광이 결합된 특화 사업이 많아 안정적인 소득 창출이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (92, '경상북도 포항시', '포항시는 경상북도 동해안에 위치하며, 인구수는 약 50만 명으로 면적은 약 1,127㎢입니다. 해양성 기후 덕분에 다양한 수산물이 풍부하며, 최근에는 농업과 어업이 함께 발전하고 있습니다.', '포항시는 농업과 어업이 공존하는 지역으로, 수산물뿐만 아니라 다양한 작물을 재배할 수 있는 기후적 이점이 있습니다. 또한, 귀농·귀어 정착 지원 정책이 잘 마련되어 있어 안정적으로 생활을 시작할 수 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (93, '경상북도 경주시', '경주시는 경상북도 남동부에 위치하며, 인구수는 약 25만 명으로 면적은 약 1,324㎢입니다. 사계절이 뚜렷하며, 농업과 관광업이 공존하는 지역입니다. 전통문화와 역사 유적이 풍부하여 관광객 유입도 많습니다.', '경주시는 귀농인 지원 정책이 잘 마련되어 있어 농업과 관광을 연계한 6차 산업이 활발히 진행되고 있습니다. 사과와 딸기 등 고소득 작물 재배가 많아 안정적인 수익 창출이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (94, '경상북도 김천시', '김천시는 경상북도 남서부에 위치하며, 인구수는 약 14만 명으로 면적은 약 1,009㎢입니다. 경북 내륙에 자리 잡고 있어 사과와 포도 재배가 활발하며, 축산업도 함께 발달한 지역입니다.', '김천시는 귀농인을 위한 다양한 교육 프로그램과 정부 지원이 체계적으로 마련되어 있어 농업 기반을 다지기 유리합니다. 특히 포도와 사과 농가가 밀집해 있어 재배 기술을 배우기 쉽고, 직거래 활성화로 안정적인 수익이 보장됩니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (95, '경상북도 안동시', '안동시는 경상북도 북부에 위치하며, 인구수는 약 16만 명으로 면적은 약 1,522㎢입니다. 전통 농업과 문화유산이 어우러진 지역으로, 사과와 생강 생산이 활발합니다', '안동시는 귀농인을 위한 체계적인 교육과 다양한 지원 정책이 마련되어 있어 안정적인 정착이 가능합니다. 특히 사과와 생강 같은 고부가가치 작물에 대한 연구와 기술 지원이 활발하여 농가 소득 증대에 기여하고 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (96, '경상북도 구미시', '구미시는 경상북도 중서부에 위치하며, 인구수는 약 41만 명으로 면적은 약 615㎢입니다. 산업단지가 발달해 있어 생활 인프라가 잘 갖춰져 있으며, 농업과 축산업도 함께 발달한 지역입니다.', '구미시는 농업과 산업이 공존하여 귀농자들이 농산물 판매처 확보가 용이합니다. 특히 수박과 양파 같은 작물이 많이 재배되며, 농업 교육 시설과 인프라가 잘 갖춰져 있어 안정적인 정착이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (97, '경상북도 영주시', '영주시는 경상북도 북부 내륙에 위치하며, 인구수는 약 10만 명으로 면적은 약 669㎢입니다. 사계절이 뚜렷하고 비교적 온화한 기후를 가지며, 사과와 인삼 재배로 유명합니다. 특히 산간 지역의 청정 환경 덕분에 고품질 농산물이 많이 생산됩니다.', '영주시는 귀농 지원 정책이 잘 마련되어 있으며, 특히 사과와 인삼을 중심으로 한 특산물 생산이 활발합니다. 사과 브랜드화를 통해 안정적인 판로를 확보하고 있으며, 농업기술센터에서 귀농인 교육과 기술 지원을 활발히 제공하고 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (98, '경상북도 영천시', '영천시는 경상북도 남동부에 위치하며, 인구수는 약 10만 명으로 면적은 약 919㎢입니다. 사계절이 뚜렷하며 일조량이 풍부해 포도와 복숭아 등 과수 재배에 적합합니다. 대구광역시와 인접하여 물류와 유통 인프라가 잘 갖춰져 있습니다.', '영천시는 포도와 복숭아 재배에 최적화된 환경을 갖추고 있어 과수 농업의 중심지로 자리 잡고 있습니다. 귀농을 고려하는 이들에게 농업 기술과 판로 확보를 위한 교육과 지원이 잘 마련되어 있어 안정적인 귀농 생활이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (99, '경상북도 상주시', '상주시는 경상북도 중서부에 위치하며, 인구수는 약 10만 명으로 면적은 약 1,254㎢입니다. 내륙 중심부에 자리 잡고 있어 교통이 편리하며, 농업 중심 지역으로 특히 곶감과 사과 생산이 활발합니다.', '상주시는 곶감과 사과를 비롯하여 다양한 과수 재배가 활발하며, 정부의 귀농 지원 정책이 잘 마련되어 있어 초보 귀농자도 쉽게 정착할 수 있습니다. 농업 교육 시설이 잘 갖춰져 있어 재배 기술 습득에 용이합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (100, '경상북도 문경시', '문경시는 경상북도 서부에 위치하며, 인구수는 약 7만 명으로 면적은 약 911㎢입니다. 산간 지역이 많아 고랭지 농업이 발달해 있으며, 특히 오미자와 사과 생산이 유명합니다.', '문경시는 고랭지 농업에 유리한 기후와 지형 덕분에 오미자와 사과 같은 고품질 농산물 생산이 가능합니다. 귀농 정책과 농업 교육도 잘 마련되어 있어 귀농 생활을 시작하기 좋은 환경입니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (101, '경상북도 경산시', '경산시는 경상북도 남부에 위치하며, 인구수는 약 28만 명으로 면적은 약 412㎢입니다. 대구와 가까워 접근성이 뛰어나며, 온난한 기후 덕분에 복숭아와 포도 재배가 활발합니다.', '경산시는 복숭아와 포도의 주산지로, 고품질 과일 브랜드화에 집중하고 있습니다. 귀농인을 위한 다양한 교육 프로그램과 판로 지원 사업이 잘 마련되어 있어 안정적인 귀농 생활이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (102, '경상북도 군위군', '군위군은 경상북도 북서부에 위치하며, 인구수는 약 2만 명으로 면적은 약 614㎢입니다. 산지와 평야가 적절히 조화를 이루고 있어 다양한 농업 활동이 이루어지고 있습니다.', '군위군은 사과와 고구마 재배에 유리한 환경을 갖추고 있으며, 귀농자들을 위한 다양한 지원 정책과 기술 교육이 잘 마련되어 있어 안정적인 농업 정착이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (103, '경상북도 의성군', '의성군은 경상북도 중북부에 위치하며, 인구수는 약 5만 명으로 면적은 약 1,163㎢입니다. 온화한 기후와 넓은 평야 덕분에 사과와 마늘 생산이 활발합니다.', '의성군은 사과와 마늘을 중심으로 한 농업이 활성화되어 있으며, 농업기술센터에서 다양한 농업 교육을 제공하여 안정적인 귀농 생활을 돕고 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (104, '경상북도 청송군', '청송군은 경상북도 북동부에 위치하며, 인구수는 약 2만 명으로 면적은 약 846㎢입니다. 산간 지역이 많아 사과와 고랭지 채소 재배에 유리합니다.', '청송군은 청정 자연을 활용한 사과 재배가 활발하며, 전국적으로 유명한 청송사과 브랜드를 육성하고 있습니다. 귀농자들을 위한 교육과 정착 지원도 잘 이루어지고 있어 안정적인 농업 활동이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (105, '경상북도 영양군', '영양군은 경상북도 북동부에 위치하며, 인구수는 약 1만 명으로 면적은 약 815㎢입니다. 고랭지 기후 덕분에 고품질 채소 재배가 활발하며, 특히 고추와 배추 생산이 많습니다.', '영양군은 귀농 정착 지원과 농업 교육 프로그램이 잘 마련되어 있어 다양한 작물 재배에 도전할 수 있습니다. 특히 고추와 배추 농가가 밀집해 있어 기술 습득이 용이합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (106, '경상북도 영덕군', '영덕군은 경상북도 동해안에 위치하여 해안과 산지가 조화를 이루는 지역입니다. 인구수는 약 4만 명으로, 면적은 약 741㎢입니다. 동해안 특유의 온난하고 습한 해양성 기후로 인해 해산물과 농업이 공존하며, 특히 사과와 배추가 많이 재배됩니다. 해안선을 따라 다양한 해산물이 잡히며, 청정 자연을 기반으로 한 농업 활동이 활발합니다.', '영덕군은 귀농과 귀어를 동시에 고려할 수 있는 지역으로, 해안과 농촌이 조화를 이루어 다각적인 생활이 가능합니다. 특히 사과와 배추 생산이 활발하여 안정적인 소득 기반을 마련할 수 있으며, 동해안 특유의 수산물과 결합한 복합 농업이 가능합니다. 농업 기술 교육과 해양 관련 직업 교육도 잘 마련되어 있어 다양한 방향으로 귀농·귀어 활동이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (107, '경상북도 청도군', '청도군은 경상북도 남서부에 위치하여 인구수는 약 4만 명, 면적은 약 694㎢입니다. 따뜻하고 온화한 기후 덕분에 다양한 농작물 재배가 가능하며, 특히 감과 사과가 유명합니다. 청도 감은 전국적으로 명성이 높으며, 곶감으로 가공하여 부가가치를 높이고 있습니다.', '청도군은 귀농을 고려하는 사람들에게 적합한 환경을 갖추고 있습니다. 감과 사과를 이용한 농산물 가공 산업이 발달해 있어 소득 창출의 기회가 많습니다. 또한, 청도 특산물인 감을 활용한 다양한 브랜드 상품이 있어 판로 확보가 용이합니다. 귀농 정책과 농업 교육도 잘 이루어져 있어 초보 농업인도 안정적으로 정착할 수 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (108, '경상북도 고령군', '고령군은 경상북도 남서부에 위치하며 인구수는 약 3만 명, 면적은 약 382㎢입니다. 낙동강을 따라 펼쳐진 비옥한 평야가 많아 다양한 작물 재배에 적합합니다. 온난한 기후 덕분에 참외와 수박이 주로 재배되며, 특히 고령 참외는 전국적으로 인지도가 높습니다.', '고령군은 참외와 수박을 중심으로 한 농업 기반이 잘 조성되어 있어 소득 창출의 기회가 많습니다. 또한, 낙동강을 따라 펼쳐진 농경지 덕분에 다양한 작물 재배가 가능하여 복합 농업을 시도할 수 있습니다. 농업 기술 교육도 활발히 운영되어 귀농자들이 쉽게 정착할 수 있는 환경입니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (109, '경상북도 성주군', '성주군은 경상북도 중서부에 위치하며 인구수는 약 4만 명, 면적은 약 616㎢입니다. 따뜻한 기후와 적당한 강수량 덕분에 다양한 과일 재배가 활발하며, 특히 참외로 유명합니다. 성주 참외는 당도가 높고 육질이 우수하여 전국적으로 사랑받고 있습니다.', '성주군은 참외 재배로 대표되는 농업 지역으로, 참외 생산 노하우와 기술이 잘 갖춰져 있어 안정적인 농업 활동이 가능합니다. 또한, 참외를 이용한 가공식품 개발과 판매처 확보를 통해 소득 증대가 이루어지고 있습니다. 다양한 귀농 교육 프로그램과 농업 지원 정책이 잘 마련되어 있어 귀농 초보자도 부담 없이 시작할 수 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (110, '경상북도 칠곡군', '칠곡군은 경상북도 중서부에 위치하며 인구수는 약 12만 명, 면적은 약 451㎢입니다. 대구광역시와 인접하여 교통이 편리하며, 농업과 산업이 조화를 이루는 지역입니다. 온화한 기후와 비옥한 토양 덕분에 다양한 작물 재배가 가능합니다.', '칠곡군은 대구와 근접하여 농산물 직거래가 용이하며, 다양한 농업 지원 정책이 잘 마련되어 있습니다. 특히 고구마와 양파를 중심으로 한 농업이 활발하며, 귀농자를 위한 체계적인 교육과 지원이 있어 안정적인 정착이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (111, '경상북도 예천군', '예천군은 경상북도 중북부에 위치하며, 인구수는 약 4만 명, 면적은 약 660㎢입니다. 사계절이 뚜렷하며, 특히 추운 겨울과 따뜻한 여름이 뚜렷하여 과일 재배에 적합합니다. 사과와 수박을 비롯한 다양한 과수 작물이 많이 재배됩니다.', '예천군은 과수 농업에 유리한 환경을 갖추고 있으며, 사과와 수박의 안정적인 판로를 확보하고 있습니다. 귀농인을 위한 지원 정책이 잘 마련되어 있으며, 농업 교육과 판로 확보에 도움을 주는 프로그램도 활발하게 운영되고 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (112, '경상북도 봉화군', '봉화군은 경상북도 북부 산간 지역에 위치하며 인구수는 약 3만 명, 면적은 약 1,202㎢입니다. 고산지대 특유의 청정 자연 환경 덕분에 고랭지 농업이 활발하며, 사과와 감자가 주요 작물입니다.', '봉화군은 고랭지 농업에 적합한 기후를 활용하여 사과와 감자를 중심으로 한 농업이 발달하였습니다. 특히 청정 자연을 활용한 친환경 농업도 활발히 이루어지고 있어 고품질 농산물을 생산할 수 있습니다. 귀농 지원 정책과 기술 교육이 잘 갖춰져 있어 안정적인 농업 정착이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (113, '경상북도 울진군', '울진군은 경상북도 동해안에 위치하며 인구수는 약 5만 명, 면적은 약 992㎢입니다. 해안과 산이 조화를 이루어 다양한 자연환경을 가지고 있으며, 특히 해양성 기후 덕분에 겨울이 온화하고 여름이 비교적 시원합니다.', '울진군은 해양 자원을 활용한 어업과 농업이 조화를 이루고 있어 귀농·귀어를 동시에 고려할 수 있는 지역입니다. 감자와 고구마 재배가 활발하며, 어촌 체험과 농업 체험이 결합된 복합 모델이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (114, '경상북도 울릉군', '울릉군은 경상북도 동쪽 끝에 위치한 섬 지역으로, 인구수는 약 1만 명, 면적은 약 73㎢입니다. 해양성 기후로 인해 겨울이 온화하고 여름이 서늘하여 독특한 자연 생태를 이루고 있습니다. 울릉도와 독도를 포함하고 있으며, 해양 자원과 고유 생태계 덕분에 관광업과 수산업이 활발합니다.', '울릉군은 섬 특유의 청정 자연을 활용한 친환경 농업이 발달해 있으며, 특히 울릉 약초와 고구마, 옥수수가 대표적인 작물입니다. 또한 해양 자원을 활용한 수산업과 농업이 조화를 이루고 있어 다각적인 소득 창출이 가능합니다. 울릉 특산물로 만든 가공식품 브랜드화도 활발하여 귀농과 귀어를 동시에 고려할 수 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (115, '경상남도 창원시', '창원시는 경상남도 남동부에 위치하며 인구수는 약 103만 명, 면적은 약 748㎢입니다. 해안과 평야가 조화를 이루며, 따뜻한 남해안 기후 덕분에 다양한 농업과 산업이 발달하였습니다. 특히 단감과 풋고추 생산이 활발하여 고품질 농산물로 명성을 얻고 있습니다.', '창원시는 남해안의 온화한 기후 덕분에 고품질의 농산물을 생산할 수 있는 지역으로, 단감과 풋고추의 전국적인 생산 거점입니다. 또한 대규모 농산물 가공단지와 유통망이 잘 갖춰져 있어 판로 확보에도 유리합니다. 귀농인을 위한 다양한 정책과 지원 프로그램이 마련되어 있어 안정적인 정착이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (116, '경상남도 진주시', '진주시는 경상남도 서부에 위치하며 인구수는 약 34만 명, 면적은 약 712㎢입니다. 온난하고 습윤한 기후 덕분에 다양한 농작물 재배에 유리하며, 특히 호박과 풋고추가 주요 생산물입니다. 문화와 교육의 도시로도 알려져 있어 생활 인프라가 잘 갖춰져 있습니다.', '진주시는 농업과 교육, 문화가 조화를 이루며 생활 여건이 뛰어난 지역입니다. 호박과 풋고추를 중심으로 한 농업이 활발하며, 다양한 지원 사업으로 인해 귀농인의 정착이 수월합니다. 농산물 직거래 활성화 정책 덕분에 안정적인 소득을 기대할 수 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (117, '경상남도 통영시', '통영시는 경상남도 남해안에 위치하며 인구수는 약 13만 명, 면적은 약 240㎢입니다. 해안선을 따라 해산물 생산이 활발하며, 수산업과 농업이 공존하는 지역입니다. 온난하고 해양성 기후로 인해 귤과 시금치가 잘 자라며, 수산물 가공업이 발달해 있습니다.', '통영시는 어업과 농업을 결합한 복합 소득 모델을 개발할 수 있는 지역입니다. 귤과 시금치 같은 고부가가치 작물 재배가 활발하며, 해양 관광과 연계하여 농촌 체험 프로그램을 운영할 수 있습니다. 다양한 지원 사업과 교육 프로그램이 귀농·귀어 희망자에게 유리한 환경을 제공합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (118, '경상남도 사천시', '사천시는 경상남도 남서부에 위치하며 인구수는 약 12만 명, 면적은 약 398㎢입니다. 해양과 평야가 조화를 이루며, 사계절이 뚜렷하고 온난한 기후 덕분에 다양한 농작물과 어종이 풍부합니다. 단감과 딸기가 주로 재배되며, 항공 산업과 어업이 발달해 있습니다.', '사천시는 항공산업과 농업이 조화를 이루는 복합 산업 도시로, 귀농·귀어를 동시에 고려할 수 있는 유리한 지역입니다. 단감과 딸기 생산이 안정적이며, 농업 기술 지원과 교육 프로그램이 잘 갖춰져 있어 신생 농업인에게 적합합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (119, '경상남도 김해시', '김해시는 경상남도 동부에 위치하며 인구수는 약 54만 명, 면적은 약 463㎢입니다. 온난한 남해안 기후로 다양한 농작물 재배가 가능하며, 대파와 토마토 생산이 활발합니다. 인근 부산과의 접근성이 좋아 물류와 유통이 편리합니다.', '김해시는 농산물 직거래 활성화와 물류 인프라 덕분에 생산된 농작물의 판로 확보가 유리합니다. 대파와 토마토 등 채소류의 생산량이 많아 소득 안정성이 높으며, 농업 교육과 직거래 지원 정책이 잘 마련되어 있어 초보 농업인도 쉽게 정착할 수 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (120, '경상남도 밀양시', '밀양시는 경상남도 중부에 위치하며 인구수는 약 11만 명, 면적은 약 799㎢입니다. 평야와 산지가 조화를 이루어 농업에 유리한 환경을 가지고 있으며, 고추와 감자 생산이 활발합니다. 사계절 기후가 뚜렷하고 일조량이 풍부하여 작물 생육에 적합합니다.', '밀양시는 고추와 감자 재배에 적합한 환경 덕분에 안정적인 농업 소득을 기대할 수 있습니다. 특히 고추 농업을 중심으로 한 기술 교육과 가공식품 브랜드 육성이 활발하여 소득 다각화가 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (121, '경상남도 거제시', '거제시는 경상남도 남동부에 위치한 섬 지역으로, 인구수는 약 24만 명, 면적은 약 401㎢입니다. 해양성 기후로 인해 겨울이 온화하고 여름이 서늘하여 작물 생육에 유리합니다. 조선업과 해양관광이 발달해 있으며, 섬 특유의 청정 농업도 활발히 이루어지고 있습니다.', '거제시는 해양성 기후를 활용한 친환경 농업이 발달해 있으며, 특히 양파와 양배추 같은 채소 재배가 활발합니다. 대규모 조선업과 해양관광 산업 덕분에 농산물 소비가 높아 직거래 시장 활성화에도 유리합니다. 귀농인을 위한 다양한 지원 사업이 마련되어 있어 안정적으로 정착할 수 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (122, '경상남도 양산시', '양산시는 경상남도 동부에 위치하며 인구수는 약 35만 명, 면적은 약 485㎢입니다. 부산과 울산을 잇는 교통 요지로 접근성이 뛰어나며, 온난하고 습윤한 기후 덕분에 다양한 채소와 과일 재배가 가능합니다.', '양산시는 수도권과의 접근성이 우수하여 농산물 직거래 시장이 활발하며, 특히 당근과 딸기 등 고부가가치 작물이 잘 자랍니다. 도시 근교 농업이 발달하여 귀농·귀촌인들이 비교적 쉽게 정착할 수 있는 환경을 갖추고 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (123, '경상남도 의령군', '의령군은 경상남도 중부에 위치하며 인구수는 약 2만 명, 면적은 약 482㎢입니다. 비옥한 평야와 온화한 기후로 인해 다양한 농업이 발달하였으며, 특히 양파와 호박 재배가 활발합니다.', '의령군은 전통적인 농업 중심지로 다양한 농업 지원 정책과 농업 기술 교육이 잘 갖춰져 있어 귀농 초기 정착에 유리합니다. 특히 양파와 호박 생산량이 많아 안정적인 소득 확보가 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (124, '경상남도 함안군', '함안군은 경상남도 중서부에 위치하며 인구수는 약 6만 명, 면적은 약 417㎢입니다. 비옥한 평야와 온난한 기후 덕분에 수박과 무 재배가 활발하여 농업 소득 기반이 안정적입니다.', '함안군은 수박과 무의 생산 거점으로, 고품질 농산물을 생산하여 전국으로 유통하고 있습니다. 귀농인을 위한 농업 기술 교육과 지원 정책이 잘 마련되어 있어 안정적인 농업 생활을 시작할 수 있는 지역입니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (125, '경상남도 창녕군', '창녕군은 경상남도 중서부에 위치하며 인구수는 약 6만 명, 면적은 약 533㎢입니다. 따뜻하고 습윤한 기후 덕분에 마늘과 양파 재배에 유리하여 전국적인 생산지로 유명합니다.', '창녕군은 마늘과 양파의 주산지로, 고품질 농산물 생산을 위해 다양한 지원 사업이 운영되고 있습니다. 농업 기반이 탄탄하며 귀농인을 위한 교육과 농업 기술 지원도 활발하여 안정적으로 농업 생활을 시작할 수 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (126, '경상남도 남해군', '남해군은 경상남도 남서부 해안에 위치하며 인구수는 약 4만 명, 면적은 약 357㎢입니다. 온난한 해양성 기후로 다양한 작물 재배에 유리하며, 시금치와 마늘 재배가 활발합니다.', '남해군은 온난한 기후 덕분에 시금치와 마늘의 고품질 생산이 가능하며, 해양 자원을 활용한 다양한 농업과 어업이 발달해 있습니다. 귀농·귀어를 동시에 고려할 수 있는 지역으로, 자연 친화적인 삶을 추구하는 사람들에게 적합합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (127, '경상남도 하동군', '하동군은 경상남도 서부에 위치하며 인구수는 약 5만 명, 면적은 약 676㎢입니다. 산악 지형과 섬진강 유역이 어우러진 자연환경 속에서 녹차와 딸기 재배가 활발합니다.', '하동군은 국내 최대 녹차 생산지 중 하나로, 고품질 녹차와 딸기 브랜드화가 잘 되어 있습니다. 자연친화적인 생활을 추구하는 귀농인에게 적합하며, 다양한 농업 지원 프로그램이 마련되어 있어 안정적인 귀농이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (128, '경상남도 산청군', '산청군은 경상남도 중부에 위치하며 인구수는 약 3만 명, 면적은 약 794㎢입니다. 한방 약초와 청정 농업이 발달해 있으며, 특히 딸기와 양파 재배가 활발합니다.', '산청군은 한방 농업과 고품질 작물 재배로 유명하며, 귀농인을 위한 한방 농업 교육과 지원 사업이 활발히 운영되고 있습니다. 특히 약초 재배와 연계한 농업 소득 모델이 특화되어 있어 안정적인 수익 창출이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (129, '경상남도 함양군', '함양군은 경상남도 서부에 위치하며 인구수는 약 4만 명, 면적은 약 725㎢입니다. 산지와 평야가 조화를 이루며, 청정 자연환경과 비옥한 토양 덕분에 다양한 농작물이 재배됩니다. 사계절 기후가 뚜렷하여 고랭지 채소와 과일 재배에 적합합니다.', '함양군은 한방약초와 고랭지 채소 재배가 활발하여, 농산물의 품질이 우수합니다. 귀농인을 위한 농업기술 교육과 다양한 정착 지원 프로그램이 마련되어 있어 초기 귀농 부담을 줄일 수 있습니다. 특히, 친환경 농업이 활성화되어 있어 지속 가능한 농업 환경을 갖추고 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (130, '경상남도 거창군', '거창군은 경상남도 북서부에 위치하며 인구수는 약 6만 명, 면적은 약 804㎢입니다. 산악 지형이 많아 청정 자연환경이 돋보이며, 특히 고랭지 채소와 사과 재배로 유명합니다. 기온이 낮아 고품질 작물 생산에 유리합니다.', '거창군은 귀농인을 위한 다양한 교육 프로그램과 농업 기술 지원이 잘 갖춰져 있으며, 특히 사과와 딸기 같은 고부가가치 작물의 브랜드화가 진행되고 있습니다. 농업 기반이 튼튼하여 안정적으로 농업 생활을 시작할 수 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (131, '경상남도 합천군', '합천군은 경상남도 서북부에 위치하며 인구수는 약 4만 명, 면적은 약 983㎢입니다. 사계절 기후가 뚜렷하며, 특히 평야 지역이 넓어 대규모 농업이 발달하였습니다. 합천댐과 호수 주변으로 농업용수가 풍부하여 쌀과 고구마 재배가 활발합니다.', '합천군은 평야와 산지가 어우러져 다양한 작물 재배에 적합합니다. 귀농인을 위한 교육과 자립 지원이 활발하며, 지역 농협과 연계한 판로 확보가 용이합니다. 특히 고구마와 감자의 대규모 생산이 이루어져 안정적인 소득을 기대할 수 있습니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (132, '제주도 제주시', '제주시는 제주도 북부에 위치하며 인구수는 약 50만 명, 면적은 약 978㎢입니다. 해양성 기후로 온난하고 습윤하며, 연평균 기온이 16도 내외로 사계절 내내 따뜻합니다. 감귤과 브로콜리 등 아열대 작물 재배에 적합하여 농업과 관광이 공존하는 지역입니다.', '제주시는 감귤 생산으로 유명하며, 최근에는 브로콜리와 다양한 채소 재배도 활발합니다. 귀농·귀촌인을 위한 다양한 지원 프로그램과 함께 농업 체험 관광을 연계한 창업 기회가 많아 안정적인 정착이 가능합니다.');
INSERT INTO region (region_id, region_name, basic_info, recommendation_reason) VALUES (133, '제주도 서귀포시', '서귀포시는 제주도 남부에 위치하며 인구수는 약 18만 명, 면적은 약 870㎢입니다. 온난 다습한 해양성 기후로 감귤과 만감류 재배가 활발합니다. 자연경관이 수려하여 관광산업과 농업이 조화를 이루며 성장하고 있는 도시입니다.', '서귀포시는 감귤과 만감류 등 고품질 아열대 과일 생산지로, 전국 유통망과 연계된 농산물 판매 인프라가 잘 갖춰져 있습니다. 귀농인을 위한 농업 기술 지원과 농촌 체험 관광 활성화로 안정적인 소득 기반을 갖추고 있습니다.');